import React, { useState } from 'react';
import Header from './components/Header';
import Dashboard from './components/Dashboard';
import TransferForm from './components/TransferForm';
import { bankAPI } from './services/api';

function App() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [currentView, setCurrentView] = useState('dashboard');
  const [refreshTrigger, setRefreshTrigger] = useState(0);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleTransferComplete = () => {
    // Trigger a refresh of the dashboard data
    setRefreshTrigger(prev => prev + 1);
  };

  const renderContent = () => {
    switch (currentView) {
      case 'dashboard':
        return <Dashboard key={refreshTrigger} />;
      case 'transfer':
        return <TransferForm onTransferComplete={handleTransferComplete} />;
      default:
        return <Dashboard key={refreshTrigger} />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header isMenuOpen={isMenuOpen} toggleMenu={toggleMenu} />

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Navigation Tabs */}
        <div className="mb-8">
          <nav className="flex space-x-8 border-b border-gray-200">
            <button
              onClick={() => setCurrentView('dashboard')}
              className={`py-2 px-1 border-b-2 font-medium text-sm ${
                currentView === 'dashboard'
                  ? 'border-primary-500 text-primary-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              Dashboard
            </button>
            <button
              onClick={() => setCurrentView('transfer')}
              className={`py-2 px-1 border-b-2 font-medium text-sm ${
                currentView === 'transfer'
                  ? 'border-primary-500 text-primary-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              Transfer Money
            </button>
          </nav>
        </div>

        {/* Main Content */}
        <div className="space-y-8">
          {renderContent()}
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-white border-t border-gray-200 mt-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="text-center text-gray-500">
            <p>&copy; 2024 Bank App. This is a demo application for educational purposes.</p>
            <p className="mt-2 text-sm">
              Connected to: <span className="font-mono text-xs">https://cursorworkshopserver.onrender.com</span>
            </p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
